export default function CategoryScreen() {
  const { id, name, isAdmin } = useLocalSearchParams();
  const router = useRouter();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      
      // 1. First try to get data from Firestore
      const q = query(collection(db, 'items'), where('categoryId', '==', id));
      const querySnapshot = await getDocs(q);
      
      if (!querySnapshot.empty) {
        // If Firestore has data, use it
        const fetchedProducts = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setProducts(fetchedProducts);
      } else {
        // If Firestore is empty, use local data
        console.log(`Using local data for category ${id}`);
        setProducts(initialCategoryProducts[id] || []);
      }
    } catch (err) {
      // If Firestore fails, use local data
      console.error('Firestore error, using local data:', err);
      setProducts(initialCategoryProducts[id] || []);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [id]);

  const handleProductPress = (item) => {
    router.push({
      pathname: '/product',
      params: {
        id: item.id,
        name: item.name,
        price: item.price,
        description: item.description,
        image: item.image,
      },
    });
  };

  const handleAddProduct = () => {
    router.push({
      pathname: '/admin/addItem',
      params: { categoryId: id, categoryName: name },
    });
  };

  const handleDeleteProduct = async (productId) => {
    Alert.alert(
      'Delete Product',
      'Are you sure you want to delete this item?',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Delete',
          style: 'destructive',
          onPress: async () => {
            try {
              await deleteDoc(doc(db, 'items', productId));
              fetchProducts(); // Refresh the list
            } catch (err) {
              Alert.alert('Error', 'Failed to delete: ' + err.message);
            }
          },
        },
      ]
    );
  };

  const renderProduct = ({ item }) => (
    <TouchableOpacity
      onPress={() => handleProductPress(item)}
      style={styles.productCard}
    >
      <Image 
        source={{ uri: item.image || 'https://via.placeholder.com/150' }} 
        style={styles.productImage} 
      />
      <Text style={styles.productName}>{item.name}</Text>
      <Text style={styles.productPrice}>₹{item.price}</Text>
      
      {isAdmin === 'true' && (
        <TouchableOpacity
          style={styles.deleteIcon}
          onPress={() => handleDeleteProduct(item.id)}
        >
          <Ionicons name="trash" size={20} color="#D9534F" />
        </TouchableOpacity>
      )}
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2A3F54" />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>{name}</Text>
        
        {isAdmin === 'true' && (
          <TouchableOpacity onPress={handleAddProduct} style={styles.addButton}>
            <Ionicons name="add" size={24} color="white" />
          </TouchableOpacity>
        )}
      </View>

      <FlatList
        data={products}
        keyExtractor={(item) => item.id}
        renderItem={renderProduct}
        numColumns={2}
        contentContainerStyle={styles.grid}
        ListEmptyComponent={
          <View style={styles.emptyContainer}>
            <Text style={styles.emptyText}>No products found in this category.</Text>
            {isAdmin === 'true' && (
              <TouchableOpacity 
                style={styles.emptyButton}
                onPress={handleAddProduct}
              >
                <Text style={styles.emptyButtonText}>Add First Product</Text>
              </TouchableOpacity>
            )}
          </View>
        }
      />
    </View>
  );
}

